export const scroll=(target, duration)=>{
    function animation(target){
            const currentValue=parseInt(window.scrollY)
            window.scrollBy(0, (target-currentValue)/20);
            console.log(target-currentValue);
            if(Math.abs(target-currentValue)>10) {
                requestAnimationFrame(()=>
                animation(target))
            }
    }
    requestAnimationFrame(()=>
        animation(target))
}
