function throttle(fn, wait) {
    let timer = null;
    return function (...args) {
      if (!timer) {
        timer = setTimeout(() => {
          fn.apply(this, args);
          timer = null;
        }, wait);
      }
    };
  }

  function animation(target) {
    const currentValue = parseInt(window.scrollY);
    let step = (target - currentValue) / 20;
    if (step < 0 && step > -1) {
      step = -1;
    } else if (step < 1 && step > 0) {
      step = 1;
    }
    window.scrollBy(0, step);
    console.log(Math.ceil((target - currentValue) / 20));
    if (Math.abs(target - currentValue) !== 0) {
      requestAnimationFrame(() => animation(target));
    } else {
      console.log("stop");
    }
  }

  export const scroll = throttle((target) => {
    requestAnimationFrame(() => animation(target));
  }, 2000);
