function animation(interval, target){
    const timer = setInterval(()=>{
        const currentValue=parseInt(window.scrollY)
        window.scrollBy(0, (target+40-currentValue)/50);
        if(Math.abs(target+40-currentValue)<5) {
            console.log('stop')
            clearInterval(timer)
        }
    }, interval)
}

export const scroll=(target, duration)=>{
    requestAnimationFrame(()=>
        animation(duration/50 ,target))
}
