  function animation(target) {
    const currentValue = parseInt(window.scrollY);
    window.scrollBy(0, Math.abs((target - currentValue) / 20) <= 1 ? 1 : (target - currentValue) / 20);
    // console.log(target-currentValue);
    console.log(Math.abs((target - currentValue) / 20) <= 1 ? 1 : (target - currentValue) / 20);
    if (Math.abs(target - currentValue) >= 1) {
      requestAnimationFrame(() => animation(target));
    } else {
      console.log('stop');
    }
  }
  requestAnimationFrame(() => animation(target));
};

