            // console.log(target-currentValue);
            console.log((Math.abs((target-currentValue)/20))<=1? 1:(target-currentValue)/20)
            if(Math.abs((target-currentValue))>=1) {
                requestAnimationFrame(()=>
                animation(target))
            }else{
                console.log('stop')
            }
    }
    requestAnimationFrame(()=>
        animation(target))
}
