export const scroll = (targetValue, duration) => {
    const startValue = parseInt(window.scrollY);
    const distance = targetValue - startValue;
    const startTime = performance.now();
    const easing = (t) => 1 - Math.pow(1 - t, 2); // ease-out easing function

    function animationStep() {
      const elapsed = performance.now() - startTime;
      const progress = Math.min(elapsed / duration, 1);
      const easingProgress = easing(progress);
      const currentValue = startValue + distance * easingProgress;
      window.scrollTo(0, currentValue);
      if (progress < 1) {
        requestAnimationFrame(animationStep);
      } else {
        setTimeout(() => {
          window.scrollTo(0, targetValue);
        }, 500); // Add a 0.5 second delay before stopping the animation completely
      }
    }

    requestAnimationFrame(animationStep);
  }
